export function getSelectionSortAnimations(array) {
  const animations = [];
  if (array.length <= 1) return array;
  const auxiliaryArray = array.slice();

  doSelection(array, animations);
  return animations;
}

function doSelection(mainArray, animations) {
  let n = mainArray.length;

  for (let i = 0; i < n; i++) {
    // Finding the smallest number in the subarray
    let min = i;
    for (let j = i + 1; j < n; j++) {
      animations.push([i, j]);
      animations.push([i, j]);

      if (mainArray[j] < mainArray[min]) {
        min = j;
      }
    }
    if (min != i) {
      // Swapping the elements
      let tmp = mainArray[i];
      mainArray[i] = mainArray[min];
      mainArray[min] = tmp;
      //animations.push([i, mainArray[min]]);
      animations.push([min, tmp]);
    }
  }
}
