export function getMergeSortAnimations(array) {
  const animations = [];
  const tempArray = array.slice();
  doMerge(array, 0, array.length - 1, tempArray, animations);
  return animations;
}

function doMerge(mainArray, startIdx, endIdx, tempArray, animations) {
  if (startIdx === endIdx) return;
  const midIdx = Math.floor((startIdx + endIdx) / 2);
  doMerge(tempArray, startIdx, midIdx, mainArray, animations);
  doMerge(tempArray, midIdx + 1, endIdx, mainArray, animations);
  Merge(mainArray, startIdx, midIdx, endIdx, tempArray, animations);
}

function Merge(mainArray, startIdx, midIdx, endIdx, tempArray, animations) {
  let i = startIdx;
  let j = midIdx + 1;
  let k = startIdx;

  while (i <= midIdx && j <= endIdx) {
    animations.push([i, j]); //change color
    animations.push([i, j]); //revert color
    if (tempArray[i] <= tempArray[j]) {
      animations.push([k, tempArray[i]]); //overwrite index value
      mainArray[k++] = tempArray[i++];
    } else {
      animations.push([k, tempArray[j]]); //overwrite index value
      mainArray[k++] = tempArray[j++];
    }
  }
  while (i <= midIdx) {
    animations.push([1, i, i]); //change color
    animations.push([2, i, i]); //revert color
    animations.push([3, k, tempArray[i]]); //overwrite index value
    mainArray[k++] = tempArray[i++];
  }
  while (j <= endIdx) {
    animations.push([j, j]); //change color
    animations.push([j, j]); //revert color
    animations.push([k, tempArray[j]]); //overwrite index value
    mainArray[k++] = tempArray[j++];
  }
}
