export function getSelectionSortAnimations(array) {
  const animations = [];
  if (array.length <= 1) return array;
  const auxiliaryArray = array.slice();
  doSelection(array, animations);
  return animations;
}

function selectionSortHelper(mainArray, startIdx, endIdx, auxiliaryArray, animations) {
  if (startIdx === endIdx) return;
  const middleIdx = Math.floor((startIdx + endIdx) / 2);
  selectionSortHelper(auxiliaryArray, startIdx, middleIdx, mainArray, animations);
  selectionSortHelper(auxiliaryArray, middleIdx + 1, endIdx, mainArray, animations);
  doSelection(mainArray, startIdx, middleIdx, endIdx, auxiliaryArray, animations);
}
function doSelection(mainArray, auxiliaryArray, animations) {
  let n = auxiliaryArray.length;

  for (let i = 0; i < n; i++) {
    // Finding the smallest number in the subarray
    let min = i;
    for (let j = i + 1; j < n; j++) {
      animations.push([i, j]);
      animations.push([i, j]);

      if (auxiliaryArray[j] < auxiliaryArray[min]) {
        min = j;
      }
    }
    if (min != i) {
      // Swapping the elements
      let tmp = mainArray[i];
      auxiliaryArray[i] = auxiliaryArray[min];
      auxiliaryArray[min] = tmp;
      //animations.push([i, auxiliaryArray[min]]);
      animations.push([min, tmp]);
    }
  }
}
