import React from "react";
import "./NavBar.css";

export default class NavBar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      activeAlgo: null,
    };
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(algorithm) {
    this.setState({ activeAlgo: algorithm });
  }

  render() {
    const { whenClickReset, whenScrolled, arrayMax, mergeSortClick, quickSortClick, selectionSortClick } = this.props;

    return (
      <div
        className="navbar-container"
        style={{
          width: `${window.innerWidth}px`,
          height: `50px`,
        }}
      >
        <button class="new-array-button" onClick={whenClickReset}>
          New Array
        </button>
        <button class="new-array-button" onClick={whenClickReset}>
          Start Sorting
        </button>
        <input id="changeSize" type="range" min="10" max={arrayMax} onChange={whenScrolled} />
        <button id="mergeSort" class="new-array-button" onClick={mergeSortClick}>
          Merge Sort
        </button>
        <button id="quickSort" class="new-array-button" onClick={quickSortClick}>
          Quick Sort
        </button>
        <button
          id="heapSort"
          className={this.state.activeAlgo === "heapSort" ? "currentAlgorithmButton" : "algorithmButton"}
          onClick={() => this.handleClick("heapSort")}
        >
          Heap Sort
        </button>
        {/* Try testing adding a function inside button in this component (changeColor)*/}
        <button id="selectionSort" class="new-array-button" onClick={selectionSortClick}>
          Selection Sort
        </button>
      </div>
    );
  }
}
