export function getQuickSortAnimations(arrayay) {
  const animations = [];
  // if (arrayay.length <= 1) return arrayay;
  doQuick(arrayay, low, high, animations);

  return animations;
}

function swap(array, i, j) {
  let temp = array[i];
  array[i] = array[j];
  array[j] = temp;
}

function partition(array, low, high) {
  // pivot
  let pivot = array[high];

  // Index of smaller element and
  // indicates the right position
  // of pivot found so far
  let i = low - 1;

  for (let j = low; j <= high - 1; j++) {
    // If current element is smaller
    // than the pivot
    if (array[j] < pivot) {
      // Increment index of
      // smaller element
      i++;
      swap(array, i, j);
    }
  }
  swap(array, i + 1, high);
  return i + 1;
}

function doQuick(array, low, high, animations) {
  if (low < high) {
    // pi is partitioning index, array[p]
    // is now at right place
    let pi = partition(array, low, high);

    // Separately sort elements before
    // partition and after partition
    doQuick(array, low, pi - 1);
    doQuick(array, pi + 1, high);
  }
}
