import React from 'react';
import './AlgoViz.css';


const PRIMARY_COLOR = 'tomato';

export default class AlgoViz extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      array: [],
    };
    this.n_bars = 10

  }

  componentDidMount() {
    this.resetArray();
  }

  resetArray() {
    const array = [];

    for (let i = 0; i < this.n_bars; i++) {
      array.push(randomIntFromInterval(10, window.innerHeight*.9));
    }
    this.setState({array});
    this.arrayWidth = Math.max(Math.floor((window.innerWidth*.75)/this.n_bars)-2,1)// Left offset and margins or just do fraction 
  }
  changeArray(evt){
    alert(evt.target.value)

  }

  render() {
    const {array} = this.state;

    return (
      <div className="array-container">
        {array.map((value, idx) => (
          <div 
          className="array-bar" 
          key={idx}
          style={{
            width: `${this.arrayWidth}px`,
            backgroundColor: PRIMARY_COLOR,
            height: `${value}px`
          }}></div>
        ))}
        <button onClick={() => this.resetArray()}>Generate New Array</button>
        <input
          id="changeSize"
          type="range"
          min="0"
          max="100"
          onChange={this.changeArray}
          />
      </div>
    );
  }
}

function randomIntFromInterval(min, max) {
  // min and max included
  return Math.floor(Math.random() * (max - min + 1) + min);
}
