export function getSelectionSortAnimations(array) {
  const animations = [];
  if (array.length <= 1) return array;
  //const array = array.slice();
  selectionSortHelper(array, animations);
  return animations;
}

function selectionSortHelper(array, animations) {
  //selectionSortHelper(array, array, animations);
  //selectionSortHelper(array, array, animations);
  doSelection(array, animations);
}
function doSelection(array, animations) {
  let n = array.length;

  for (let i = 0; i < n; i++) {
    // Finding the smallest number in the subarray
    // console.log(array);
    // console.log(i);
    let min = i;
    for (let j = i + 1; j < n; j++) {
      animations.push([min, j]);
      animations.push([min, j]);

      if (array[j] < array[min]) {
        min = j;
      }
    }

    // console.log(array[min]);
    if (min !== i) {
      // Swapping the elements
      let tmp = array[i];
      array[i] = array[min];
      array[min] = tmp;

      animations.push([min, array[min], i, array[i]]); // index values swapped
    } else {
      animations.push([min, array[min], i, array[i]]); //value unchanged
    }
  }
}
